// Summary Statistics (24h)
let timeRange = 24h;
let blockedEvents = customEvents | where timestamp > ago(timeRange) | where name == 'Navigation.Move.Blocked';
let totalBlocked = toscalar(blockedEvents | count);
let topReason = toscalar(blockedEvents | extend reason = tostring(customDimensions.reason) | summarize Count = count() by reason | top 1 by Count desc | project reason);
print ['Total Blocked Events'] = iff(totalBlocked == 0, 'No traversal friction detected ‚úì', strcat(totalBlocked, ' events')), ['Most Common Reason'] = iff(totalBlocked == 0, 'N/A', topReason), ['Time Range'] = '24 hours'
