// Gremlin Operation RU & Latency Overview (Issue #289)
let timeRange = 24h;
let fallbackTimeRange = 6h;
let minEvents = 20;
// First attempt 24h query
let events24h = customEvents
| where timestamp > ago(timeRange)
| where name == 'Graph.Query.Executed'
| extend operationName = tostring(customDimensions.operationName),
         latencyMs = todouble(customDimensions.latencyMs),
         ruCharge = todouble(customDimensions.ruCharge)
| where isnotempty(operationName);
let totalEvents24h = toscalar(events24h | count);
// Fallback to 6h if 24h volume is too low
let events6h = customEvents
| where timestamp > ago(fallbackTimeRange)
| where name == 'Graph.Query.Executed'
| extend operationName = tostring(customDimensions.operationName),
         latencyMs = todouble(customDimensions.latencyMs),
         ruCharge = todouble(customDimensions.ruCharge)
| where isnotempty(operationName);
let totalEvents6h = toscalar(events6h | count);
let use24hWindow = totalEvents24h >= minEvents;
let effectiveEvents = union
    (events24h | extend windowLabel = '24h'),
    (events6h  | extend windowLabel = '6h')
| where (use24hWindow and windowLabel == '24h') or (not(use24hWindow) and windowLabel == '6h')
| project-away windowLabel;
let totalEffective = iff(use24hWindow, totalEvents24h, totalEvents6h);
let showResults = totalEffective >= minEvents;
let results = effectiveEvents
| summarize 
    Calls = count(),
    AvgRU = round(avg(ruCharge), 2),
    MaxRU = round(max(ruCharge), 2),
    P50 = round(percentile(latencyMs, 50), 0),
    P95 = round(percentile(latencyMs, 95), 0),
    P99 = round(percentile(latencyMs, 99), 0),
    MissingRU = countif(isnull(ruCharge) or isnan(ruCharge))
  by operationName
| extend AvgRUDisplay = iff(MissingRU > 0.3 * Calls, 'n/a', tostring(AvgRU)),
         MaxRUDisplay = iff(MissingRU > 0.3 * Calls, 'n/a', tostring(MaxRU))
| project operationName, Calls, AvgRUDisplay, MaxRUDisplay, P50, P95, P99
| order by Calls desc
| take 10
| extend include = showResults;
let instabilityNotice = datatable(operationName:string, Calls:long, AvgRUDisplay:string, MaxRUDisplay:string, P50:real, P95:real, P99:real)
[
    '‚ö†Ô∏è INSTABILITY NOTICE: <20 total events in last 24h. Percentiles unreliable. Wait for more data.', long(null), '', '', real(null), real(null), real(null)
]
| extend include = not(showResults);
union results, instabilityNotice
| where include
| project operationName, Calls, AvgRUDisplay, MaxRUDisplay, P50, P95, P99

// RU vs Latency Correlation - Trend Line with Correlation Coefficient (Issue #290)
let timeRange = 2h;
let minSampleSize = 30;
let events = customEvents
| where timestamp > ago(timeRange)
| where name == 'Graph.Query.Executed'
| extend operationName = tostring(customDimensions.operationName),
         latencyMs = todouble(customDimensions.latencyMs),
         ruCharge = todouble(customDimensions.ruCharge)
| where isnotempty(operationName) and isnotnull(ruCharge) and isnotnull(latencyMs) and latencyMs > 0;
// Count missing ruCharge for edge case tracking
let totalEvents = toscalar(customEvents
| where timestamp > ago(timeRange)
| where name == 'Graph.Query.Executed'
| where isnotempty(tostring(customDimensions.operationName))
| count);
let eventsWithRU = toscalar(events | count);
let missingRUCount = totalEvents - eventsWithRU;
// Compute Pearson correlation per operation (simplified approximation)
let correlations = events
| summarize 
    n = count(),
    sumX = sum(ruCharge),
    sumY = sum(latencyMs),
    sumXY = sum(ruCharge * latencyMs),
    sumX2 = sum(ruCharge * ruCharge),
    sumY2 = sum(latencyMs * latencyMs)
  by operationName
| extend 
    numerator = (n * sumXY) - (sumX * sumY),
    denomX = sqrt((n * sumX2) - (sumX * sumX)),
    denomY = sqrt((n * sumY2) - (sumY * sumY))
| extend correlation = iff(denomX == 0 or denomY == 0, 0.0, numerator / (denomX * denomY))
| extend correlation = round(correlation, 3)
| project operationName, correlation, SampleSize = n, MeetsThreshold = iff(n >= minSampleSize, "Yes", "No")
| order by correlation desc;
let hasValidCorrelations = toscalar(correlations | where SampleSize >= minSampleSize | count);
// Return warning if no operations have sufficient samples, else show correlations
iff(hasValidCorrelations == 0,
    datatable(Notice:string) [strcat("‚ö†Ô∏è INSUFFICIENT SAMPLE WARNING: All operations have <", minSampleSize, " samples. Correlation coefficients unreliable. Wait for more data or check if telemetry is flowing. Missing RU count: ", missingRUCount)],
    correlations
)

// Partition Pressure Trend - RU% with 429 overlay (Issue #291)
// Supports dynamic MAX_RU_PER_INTERVAL parameter with fallback handling
let timeRange = 24h;
let bucketSize = 5m;
let maxRuStr = '{MaxRuPerInterval:escape}';
let hasValidConfig = maxRuStr != '' and maxRuStr != '{MaxRuPerInterval:escape}';
let maxRuParam = iff(hasValidConfig, todouble(maxRuStr), todouble(null));

// Process RU events
let ruEvents = customEvents
| where timestamp > ago(timeRange)
| where name == 'Graph.Query.Executed'
| extend ruCharge = todouble(customDimensions.ruCharge)
| where isnotnull(ruCharge);

let failures = customEvents
| where timestamp > ago(timeRange)
| where name == 'Graph.Query.Failed'
| extend statusCode = toint(customDimensions.statusCode)
| where statusCode == 429;

// Aggregate RU and 429 counts per bucket (including zero-RU intervals)
let allBuckets = range timestamp from ago(timeRange) to now() step bucketSize
| project bucket = bin(timestamp, bucketSize);

let ruByBucket = ruEvents
| summarize TotalRU = sum(ruCharge) by bucket = bin(timestamp, bucketSize);

let throttleByBucket = failures
| summarize ThrottleCount = count() by bucket = bin(timestamp, bucketSize);

// Calculate RU% with proper zero handling
let pressureData = allBuckets
| join kind=leftouter ruByBucket on bucket
| join kind=leftouter throttleByBucket on bucket
| extend TotalRU = coalesce(TotalRU, 0.0),
         ThrottleCount = coalesce(ThrottleCount, 0),
         RUPercent = iff(hasValidConfig and isnotnull(maxRuParam) and maxRuParam > 0, round(100.0 * coalesce(TotalRU, 0.0) / maxRuParam, 2), todouble(null))
| project timestamp = bucket, RUPercent, ThrottleCount, TotalRU, Threshold70 = 70.0, Threshold80 = 80.0
| order by timestamp asc;

// Return data (if config missing, RUPercent will be null and message shown in title)
pressureData

// Sustained High RU Annotation (Issue #291)
// Detects RU% >70% for ‚â•3 consecutive 5-minute intervals
let timeRange = 24h;
let bucketSize = 5m;
let maxRuStr = '{MaxRuPerInterval:escape}';
let hasValidConfig = maxRuStr != '' and maxRuStr != '{MaxRuPerInterval:escape}';
let maxRuParam = iff(hasValidConfig, todouble(maxRuStr), todouble(null));

// Early exit if no config
let noConfig = datatable(Message:string) ["‚ÑπÔ∏è Configure MAX_RU_PER_INTERVAL parameter to enable sustained pressure detection"];

// Calculate RU% per bucket
let ruEvents = customEvents
| where timestamp > ago(timeRange)
| where name == 'Graph.Query.Executed'
| extend ruCharge = todouble(customDimensions.ruCharge)
| where isnotnull(ruCharge);

let allBuckets = range timestamp from ago(timeRange) to now() step bucketSize
| project bucket = bin(timestamp, bucketSize);

let ruByBucket = ruEvents
| summarize TotalRU = sum(ruCharge) by bucket = bin(timestamp, bucketSize);

let pressureData = allBuckets
| join kind=leftouter ruByBucket on bucket
| extend TotalRU = coalesce(TotalRU, 0.0),
         RUPercent = iff(hasValidConfig and isnotnull(maxRuParam) and maxRuParam > 0, round(100.0 * coalesce(TotalRU, 0.0) / maxRuParam, 2), 0.0)
| project timestamp = bucket, RUPercent
| order by timestamp asc;

// Detect sustained high RU (>=3 consecutive intervals >70%)
let sustainedHighRU = pressureData
| extend IsHigh = RUPercent > 70.0
| serialize rn = row_number()
| extend prevIsHigh1 = prev(IsHigh, 1), prevIsHigh2 = prev(IsHigh, 2)
| where IsHigh and prevIsHigh1 and prevIsHigh2
| summarize 
    StartTime = min(timestamp), 
    EndTime = max(timestamp), 
    ConsecutiveIntervals = count(),
    MaxRU = max(RUPercent),
    AvgRU = round(avg(RUPercent), 1)
| extend Message = strcat("üö® SUSTAINED HIGH RU: ", ConsecutiveIntervals, " consecutive intervals >70% (avg: ", AvgRU, "%, max: ", MaxRU, "%)")
| project StartTime, EndTime, ConsecutiveIntervals, AvgRU, MaxRU, Message;

// Return no-config message or annotations
iff(not(hasValidConfig), noConfig, sustainedHighRU)

// Missing RU Warning Banner (Issue #296)
let timeRange = 24h;
let successEvents = customEvents
| where timestamp > ago(timeRange)
| where name == 'Graph.Query.Executed'
| extend operationName = tostring(customDimensions.operationName),
         ruCharge = todouble(customDimensions.ruCharge)
| where isnotempty(operationName);
let totalEvents = toscalar(successEvents | count);
let missingRU = toscalar(successEvents | where isnull(ruCharge) | count);
let missingPercent = iff(totalEvents > 0, round(100.0 * missingRU / totalEvents, 1), 0.0);
let showWarning = missingPercent > 30.0;
iff(showWarning,
    datatable(Warning:string) [strcat("‚ö†Ô∏è RU DATA INCOMPLETE: ", missingPercent, "% of success events missing ruCharge values. RU/Call metrics may be unreliable. Check Graph.Query.Executed telemetry instrumentation.")],
    datatable(Status:string) ["‚úÖ RU data coverage adequate (<30% missing)"]
)

// Overall Failure Rate Notes (Issue #296)
let timeRange = 24h;
let successEvents = customEvents
| where timestamp > ago(timeRange)
| where name == 'Graph.Query.Executed';
let failureEvents = customEvents
| where timestamp > ago(timeRange)
| where name == 'Graph.Query.Failed';
let totalSuccess = toscalar(successEvents | count);
let totalFailure = toscalar(failureEvents | count);
let totalCalls = totalSuccess + totalFailure;
let overallFailureRate = iff(totalCalls > 0, round(100.0 * totalFailure / totalCalls, 2), 0.0);
let isLowPriority = overallFailureRate < 1.0 and totalCalls >= 100;
iff(isLowPriority,
    datatable(Note:string) [strcat("‚ÑπÔ∏è LOW PRIORITY: Overall failure rate is ", overallFailureRate, "% (<1%). System reliability is good. Optimization tasks may be deferred unless specific operations show high cost or latency.")],
    iff(totalCalls < 100,
        datatable(Note:string) [strcat("‚ÑπÔ∏è INSUFFICIENT DATA: Only ", totalCalls, " total operations in 24h. Wait for more traffic to assess priority.")],
        datatable(Note:string) [strcat("‚ö†Ô∏è MONITOR: Overall failure rate is ", overallFailureRate, "% (‚â•1%). Review high-failure operations above.")]
    )
)
